// SPDX-License-Identifier: MIT

pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/utils/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/proxy/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";

import "../token/IAUSD.sol";
import "./Permissions.sol";
import "./IController.sol";
import "../dao/Artha.sol";
import "../token/AUSD.sol";

contract Controller is Initializable, IController, Permissions{

    using EnumerableSet for EnumerableSet.AddressSet;
    using SafeERC20 for IERC20;

    IAUSD public override ausd;
    IERC20 public override artha;

    EnumerableSet.AddressSet internal poolWrappers;

    uint256 public override balancerRewardAmount = 500 ether;
    uint256 public override issuerRewardAmount = 500 ether;

    mapping (bytes32 => address) public override poolPriceFeeds;
    mapping (bytes32 => address) public override poolIssuers;
    // solhint-disable var-name-mixedcase
    uint256 public override BASIS_POINT = 10000;
    // solhint-disable var-name-mixedcase    
    uint256 public override LIMIT = 50;

    address public override balancer;

    function init() external override initializer {

        _setupGovernor(msg.sender);

        AUSD _ausd = new AUSD(address(this));
        _setAUSD(address(_ausd));

        Artha _artha = new Artha(address(this), msg.sender);
        _setArtha(address(_artha));

    }

    /// @notice sets ausd address to a new address
    /// @param token new ausd address
    function setAUSD(address token) public override onlyGovernor {
        _setAUSD(token);
    }

    /// @notice sets Artha address to a new address
    /// @param token new Artha address
    function setArtha(address token) public override onlyGovernor {
        _setArtha(token);
    }

    function setBalancer(address _balancer) public override onlyGovernor{
        revokeBalancer(balancer);
        revokeMinter(balancer);
        balancer = _balancer;
        grantBalancer(_balancer);
        grantMinter(_balancer);
    }

    function poolWrappersLength() public view override returns (uint256) {
        return poolWrappers.length();
    }

    function poolWrapperAt(uint256 i) public view override returns (address) {
        return poolWrappers.at(i);
    }

    ///
    function setLimit(uint256 amount) public override onlyGovernor {
        LIMIT = amount;
    }

    function addPoolWrapper(address poolWrapper) public override onlyGovernor {
        poolWrappers.add(poolWrapper);
        grantPoolWrapper(poolWrapper);
    }

    function removePoolWrapper(address poolWrapper) public override onlyGovernor{
        poolWrappers.remove(poolWrapper);
        revokePoolWrapper(poolWrapper);
    }

    function setPoolPriceFeed(bytes32 pool, address feed) public override onlyGovernor {
        poolPriceFeeds[pool] = feed;
    }

    function setPoolIssuer(bytes32 pool, address issuer) public override onlyGovernor {
        if(poolIssuers[pool] != address(0)){
            revokeMinter(poolIssuers[pool]);
        }
        poolIssuers[pool] = issuer;
        grantMinter(issuer);
    }

    function setBalancerReward(uint256 amount) public override onlyGovernor {
        balancerRewardAmount = amount;
    }

    function setIssuerReward(uint256 amount) public override onlyGovernor {
        issuerRewardAmount = amount;
    }

    function transferAsset(address asset, address dst, uint256 amount) public override onlyGovernor {
        IERC20(asset).safeTransfer(dst, amount);
    }

    function _setAUSD(address token) internal {
        ausd = IAUSD(token);
        emit AUSDUpdate(token);
    }

    function _setArtha(address token) internal {
        artha = IERC20(token);
        emit ArthaUpdate(token);
    }    

    /// @notice sends AGT tokens from treasury to an address
    /// @param to the address to send AGT to
    /// @param amount the amount of AGT to send
    function allocateArtha(address to, uint256 amount)
        external
        override
        onlyGovernor
    {
        IERC20 _artha = artha;
        _artha.safeTransfer(to, amount);

        emit ArthaAllocation(to, amount);
    }


}

