pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./IPermissions.sol";

/// @title Access control module for Controller
/// @author Artha Protocol
contract Permissions is IPermissions, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant GOVERN_ROLE = keccak256("GOVERN_ROLE");
    bytes32 public constant GUARDIAN_ROLE = keccak256("GUARDIAN_ROLE");
    bytes32 public constant BALANCER_ROLE = keccak256("BALANCER_ROLE");
    bytes32 public constant POOL_WRAPPER_ROLE = keccak256("POOL_WRAPPER_ROLE");

    constructor() public {
        // Appointed as a governor so guardian can have indirect access to revoke ability
        _setupGovernor(address(this));

        _setRoleAdmin(MINTER_ROLE, GOVERN_ROLE);
        _setRoleAdmin(GOVERN_ROLE, GOVERN_ROLE);
        _setRoleAdmin(GUARDIAN_ROLE, GOVERN_ROLE);
        _setRoleAdmin(BALANCER_ROLE, GOVERN_ROLE);
        _setRoleAdmin(POOL_WRAPPER_ROLE, GOVERN_ROLE);
    }

    modifier onlyGovernor() {
        require(
            isGovernor(msg.sender),
            "Permissions: Caller is not a governor"
        );
        _;
    }

    modifier onlyGuardian() {
        require(isGuardian(msg.sender), "Permissions: Caller is not a guardian");
        _;
    }

    modifier onlyBalancer() {
        require(isBalancer(msg.sender), "Permissions: Caller is not a balancer");
        _;
    }

    modifier onlyMinter() {
        require(isMinter(msg.sender), "Permissions: Caller is not a minter");
        _;
    }


    /// @notice creates a new role to be maintained
    /// @param role the new role id
    /// @param adminRole the admin role id for `role`
    /// @dev can also be used to update admin of existing role
    function createRole(bytes32 role, bytes32 adminRole)
        public
        override
        onlyGovernor
    {
        _setRoleAdmin(role, adminRole);
    }

    /// @notice grants minter role to address
    /// @param minter new minter
    function grantMinter(address minter) public override onlyGovernor {
        grantRole(MINTER_ROLE, minter);
    }

    /// @notice grants governor role to address
    /// @param governor new governor
    function grantGovernor(address governor) public override onlyGovernor {
        grantRole(GOVERN_ROLE, governor);
    }

    /// @notice grants guardian role to address
    /// @param guardian new guardian
    function grantGuardian(address guardian) public override onlyGovernor {
        grantRole(GUARDIAN_ROLE, guardian);
    }

    /// @notice grants balancer role to address
    /// @param balancer new balancer
    function grantBalancer(address balancer) public override onlyGovernor {
        grantRole(BALANCER_ROLE, balancer);
    }

    /// @notice grants balancer role to address
    /// @param poolWrapper new poolWrapper
    function grantPoolWrapper(address poolWrapper) public override onlyGovernor {
        grantRole(POOL_WRAPPER_ROLE, poolWrapper);
    }


    /// @notice revokes minter role from address
    /// @param minter ex minter
    function revokeMinter(address minter) public override onlyGovernor {
        revokeRole(MINTER_ROLE, minter);
    }


    /// @notice revokes governor role from address
    /// @param governor ex governor
    function revokeGovernor(address governor) public override onlyGovernor {
        revokeRole(GOVERN_ROLE, governor);
    }

    /// @notice revokes guardian role from address
    /// @param guardian ex guardian
    function revokeGuardian(address guardian) public override onlyGovernor {
        revokeRole(GUARDIAN_ROLE, guardian);
    }

    /// @notice revokes balancer role from address
    /// @param balancer ex balancer
    function revokeBalancer(address balancer) public override onlyGovernor {
        revokeRole(BALANCER_ROLE, balancer);
    }

    /// @notice revokes balancer role from address
    /// @param poolWrapper ex poolWrapper
    function revokePoolWrapper(address poolWrapper) public override onlyGovernor {
        revokeRole(POOL_WRAPPER_ROLE, poolWrapper);
    }


    /// @notice revokes a role from address
    /// @param role the role to revoke
    /// @param account the address to revoke the role from
    function revokeOverride(bytes32 role, address account)
        public
        override
        onlyGuardian
    {
        require(role != GOVERN_ROLE, "Permissions: Guardian cannot revoke governor");

        // External call because this contract is appointed as a governor and has access to revoke
        this.revokeRole(role, account);
    }


    /// @notice checks if address is a minter
    /// @param _address address to check
    /// @return true _address is a minter
    function isMinter(address _address) public view override returns (bool) {
        return hasRole(MINTER_ROLE, _address);
    }

    /// @notice checks if address is a governor
    /// @param _address address to check
    /// @return true _address is a governor
    // only virtual for testing mock override
    function isGovernor(address _address)
        public
        view
        virtual
        override
        returns (bool)
    {
        return hasRole(GOVERN_ROLE, _address);
    }

    /// @notice checks if address is a guardian
    /// @param _address address to check
    /// @return true _address is a guardian
    function isGuardian(address _address) public view override returns (bool) {
        return hasRole(GUARDIAN_ROLE, _address);
    }

    /// @notice checks if address is a balancer
    /// @param _address address to check
    /// @return true _address is a balancer
    function isBalancer(address _address) public view override returns (bool) {
        return hasRole(BALANCER_ROLE, _address);
    }

    /// @notice checks if address is a balancer
    /// @param _address address to check
    /// @return true _address is a balancer
    function isPoolWrapper(address _address) public view override returns (bool) {
        return hasRole(POOL_WRAPPER_ROLE, _address);
    }


    function _setupGovernor(address governor) internal {
        _setupRole(GOVERN_ROLE, governor);
    }
}

